/*********************************************************************
*
*       _cbDialog
*/
static void _cbDialog(WM_MESSAGE * pMsg) {
  WM_HWIN                hItem;
  WM_MESSAGE             Msg;
	PROGBAR_Handle hProg;
	
	char num[3];
  int32_t sum=0;
	int32_t i=0;

  // USER START (Optionally insert additional variables)
  // USER END
	hItem = pMsg->hWin;
	WINDOW_SetBkColor(hItem, GUI_LIGHTGRAY);
		
  switch (pMsg->MsgId) {
  case WM_INIT_DIALOG:
    hItem = WM_GetDialogItem(pMsg->hWin, ID_TEXT_0);
    TEXT_SetFont(hItem,&GUI_FontHZ_Song_16);
    TEXT_SetText(hItem, "连接板卡数量：");  
	
		hItem = WM_GetDialogItem(pMsg->hWin, ID_TEXT_1);
		TEXT_SetFont(hItem,&GUI_FontHZ_Song_16);
		if(SelfCheckItem.port_board_number == 0)
		{
			osMessagePut(WireSelfCheckEvent, WIRESELFCHECK_START_EVENT, 0);//开始自检
			i=100;
			while(i--)
			{
				osDelay(10);
				if(SelfCheckItem.state == 0) break;
			}
		}
    
		TEXT_SetText(hItem,itoa(SelfCheckItem.port_board_number,num,10)); 
    
		hItem = WM_GetDialogItem(pMsg->hWin, ID_BUTTON_OK);
		BUTTON_SetFont(hItem,&GUI_FontHZ_Song_12);
		BUTTON_SetSkinClassic(hItem);
		BUTTON_SetTextAlign(hItem, GUI_TA_HCENTER | GUI_TA_VCENTER);
    BUTTON_SetBkColor(hItem,BUTTON_CI_UNPRESSED, GUI_GREEN);
		
		hItem = WM_GetDialogItem(pMsg->hWin, ID_BUTTON_CANCEL);
		BUTTON_SetFont(hItem,&GUI_FontHZ_Song_12);
		BUTTON_SetSkinClassic(hItem);
		BUTTON_SetTextAlign(hItem, GUI_TA_HCENTER | GUI_TA_VCENTER);
    BUTTON_SetBkColor(hItem,BUTTON_CI_UNPRESSED, GUI_RED);

	
		hItem = WM_GetDialogItem(pMsg->hWin, ID_PROGBAR0);  
		PROGBAR_SetTextAlign(hItem, GUI_TA_HCENTER | GUI_TA_VCENTER);
    PROGBAR_SetFont(hItem, &GUI_FontHZ_Song_12);
		
		if(SelfCheckItem.port_board_number<=0)
		{
			hItem = WM_GetDialogItem(pMsg->hWin, ID_BUTTON_OK);
			TEXT_SetBkColor(hItem, GUI_LIGHTGRAY);
		}

    break;
	case WM_KEY:
		GUI_SendKeyMsg(GUI_KEY_TAB, 1);//改变输入焦点
		break;
	case MY_MESSAGE_WHEEL:
		break;
	case MY_MESSAGE_CANCEL://取消
		StudyFile.command=2;//study cancel
		GUI_EndDialog(pMsg->hWin,0);
		break;
	case MY_MESSAGE_OK:
		wt_SetText_Status("开始自学习");
		GUI_Exec();
		hProg = WM_GetDialogItem(pMsg->hWin, ID_PROGBAR0);
		PROGBAR_SetValue(hProg, 0);
		
		sum=add(SelfCheckItem.port_board_number*64);
		if(sum==0) 
		{	
			break;
		}
		PROGBAR_SetMinMax(hProg, 0, sum);
		//开始学习
		osMessagePut(WireStudyEvent, WIRESTUDY_START_EVENT, 0);
		while(1)
		{
			GUI_Delay(1000);
			if(StudyFile.item_index>=sum) break;
			PROGBAR_SetValue(hProg, StudyFile.item_index);
			if(StudyFile.task_status == 3)//study error
				  wt_SetText_Status("自学习错误");	
			//取消自学习
			if(BSP_GetKEY_State(KeyCancle) == 1 )
			{
				while(BSP_GetKEY_State(KeyCancle) == 1)
				{
					i++;
					osDelay(100);
				}
				if(i < 20)
				{
					StudyFile.command=2;//study cancel
					LED4_OFF();
					Msg.MsgId = MY_MESSAGE_BUTTONCANCEL;
					WM_SendMessage(pMsg->hWinSrc,&Msg);
					GUI_EndDialog(pMsg->hWin,0);
					break;
				}
			}
		}

		if(StudyFile.command!=2)//0-no operate, 1-start, 2-cancel
		{
			//开始自学习优化
			osMessagePut(WireStudyEvent, WIRESTUDY_OPTIMIZE_EVENT, 0);
			wt_SetText_Status("自学习优化");
			osDelay(500);
			for(;;)
			{
				if(StudyFile.task_status == 0)
					break;
			}
			Msg.MsgId = MY_MESSAGE_BUTTONOK;
			WM_SendMessage(pMsg->hWinSrc,&Msg);
			GUI_EndDialog(pMsg->hWin,0);
		}
		break;
	case WM_PAINT://绘制标题栏
		GUI_SetColor(GUI_DARKBLUE);
		GUI_FillRect(0,0,280,22);
		GUI_SetColor(GUI_DARKGRAY);
		GUI_SetPenSize(8);
		GUI_DrawRect(0,0,278,218);
    break;
  default:
    WM_DefaultProc(pMsg);
    break;
  }
}

